import { useState, createContext, useContext } from 'react';

const ToDoContent = createContext();

const ToDoProvider = ({children}) => {
  const [title, setTitle] = useState('');
  const [task, setTask] = useState([{title: 'This your first todo list', complete : true }]);
  const addNewTask = () => {
    setTask([...task, {title ,  complete : false }])
  } 
  const completeTask = (title) => {
    setTask([...task, { title ,  complete : true }])
  } 
  return (
    <ToDoContent.Provider value={{title, setTitle, addNewTask, completeTask, task}}>
      {children}
    </ToDoContent.Provider>
  )
}

const ToDoForm = () => {
  const { setTitle , addNewTask, title } = useContext(ToDoContent);
  return (
    <form onSubmit={addNewTask}>
      <input type="text" onChange={(e)=>setTitle(e.target.value)} value={title}/>
      <button type='submit'>Add New Task</button>
    </form>

  )
}

const ToDoList = () =>{
  const { task, completeTask } = useContext(ToDoContent);
  return (
    <ul>
      { task.map((item) => {
        return <li >{item.title} <button onClick={() => {completeTask(item.title)}}>Complete</button></li>
      })
      }
    </ul>
  )
}

function App() {
  return (
        <ToDoProvider>
          <ToDoForm/>
          <ToDoList/>
        </ToDoProvider>
  );
}

export default App;
