import { useState, createContext, useContext } from 'react';
import './App.css';

const ToDoContent = createContext();

const ToDoProvider = ({children}) => {
  const [title, setTitle] = useState('');
  const [task, setTask] = useState([{title: 'This your first todo list', complete : true }])
  const addNewTask = () => {
    setTask([...task, {title ,  complete : false }])
  } 
  const completeTask = (title) => {
    setTask([...task, { title ,  complete : true }])
  } 
  return (
    <ToDoContent.Provider value={{setTitle, addNewTask, completeTask}}>
      {children}
    </ToDoContent.Provider>
  )
}

const ToDoForm = () => {
  const {setTitle , addNewTask } = useContent(ToDoContent);
  return (
    <form onSubmit={addNewTask}>
      <input type="text" onChange={(e)=>setTitle(e.target.value)}/>
      <input type='submit'>Add New Task</input>
    </form>

  )
}

const ToDoList = () =>{
  const { task } = useContext(ToDoContent);
  return (
    <ul>
      { map.task((item) => {
        return <li >{item.title} <button onClick={(e) => {completeTask(item)}}>Complete</button></li>
      })
      }
    </ul>
  )
}

function App() {
  return (
    <div className="App">
        <ToDoProvider>
          <ToDoForm/>
          <ToDoList/>
        </ToDoProvider>

    </div>
  );
}

export default App;
