import { useState, createContext } from 'react';
import './App.css';

const ToDoContent = createContext();

const ToDoProvider = ({children}) => {
  const [title, setTitle] = useState('');
  const [task, setTask] = useState([{title: 'This your first todo list', complete : true }])
  const addNewTask = () => {
    setTask([...task, {title ,  complete : false }])
  } 
  const completeTask = (title) => {
    setTask([...task, { title ,  complete : true }])
  } 
  return (
    <ToDoContent.Provider value={{setTitle, addNewTask, completeTask}}>
      {children}
    </ToDoContent.Provider>
  )
}

