import { useState, createContext, useContext } from 'react';

const ToDoContext = createContext();

const ToDoProvider = ({children}) => {

  const [task, setTask] = useState([{title: 'This your first todo list', complete : true }]);
  const addNewTask = (title) => {

    setTask([...task, { id : task.length + 1 , title ,  complete : false }])
  } 
  const completeTask = (object) => {
    let list = [...task];
    list.map(item => {
      if(item.id === object)
      item.complete = true;
    })
    setTask(list);
  } 
  return (
    <ToDoContext.Provider value={{addNewTask, completeTask, task}}>
      {children}
    </ToDoContext.Provider>
  )
}

const ToDoForm = () => {
  const { addNewTask } = useContext(ToDoContext);
  const [title, setTitle] = useState('');
  const submit = (e) => {
    e.preventDefault();
    if(!title) {
      alert('Enter a title');
      return;
    }
    addNewTask(title);
  }
  return (
    <form onSubmit={submit}>
      <input type="text" onChange={(e)=>setTitle(e.target.value)} value={title}/>
      <button type='submit'>Add New Task</button>
    </form>

  )
}

const ToDoList = () =>{
  const { task, completeTask } = useContext(ToDoContext);
  return (
    <ul>
      { task.map((item) => {
        return <li key={item.title} style={{textDecoration : item.complete ? 'line-through' : 'none'}} >{item.title} <button style={{visibility : item.complete ? 'hidden' : 'visible'}} onClick={() => {completeTask(item)}} >Complete</button></li>
      })
      }
    </ul>
  )
}

function App() {
  return (
        <ToDoProvider>
          <ToDoForm/>
          <ToDoList/>
        </ToDoProvider>
  );
}

export default App;
