import { createContext, useEffect, useContext } from "react";
import { BrowserRouter, Routes, Route, Outlet, Link, useNavigate } from "react-router-dom";

function HomePage() {
  return <h1>Home</h1>;
}

function AboutUsPage() {
  return <h1>About us</h1>;
}

function TaskBaordPage() {
  return <h1>Task Board</h1>;
}

function ManageTaskPage() {
  const user = useContext(AuthContext);
  useIAM(user);
  return (
    <>
      <h1>Manage Task</h1>
      <ul>
        <li>
          <Link to="create">Create Task</Link>
        </li>
        <li>
          <Link to="schedule">Schedule Task</Link>
        </li>
      </ul>
      <Outlet />
    </>
  );
}

function CreateTaskPage() {
  return <h1>Create Task</h1>;
}

function ScheduleTaskPage() {
  return <h1>Schedule Task</h1>;
}

function LoginPage() {
  return <h1>Login</h1>;
}

function SignupPage() {
  return <h1>Signup</h1>;
}

function NoPage() {
  return <h1>No Page found</h1>;
}

function useAuthGuard(user){
  const navigate = useNavigate();
  useEffect(() => {
    if(!user){
      navigate('/login');
    }
  }, [user]);

}

function useIAM(user){
  const navigate = useNavigate();
  useEffect(() => {
    if(!user?.role){
      navigate('/home');
    }if(user?.role && user?.role !== 'admin'){
      navigate('/task-board');
    }
  }, [user?.role]);
}

const AuthContext = createContext();

const AuthProvider = ({children}) =>{
  const user = {
    id: Math.random(),
    role: 'admin',
  };
  return(
    <AuthContext.Provider value={{user}}>
      {children}
    </AuthContext.Provider>
  )
}

function AppLayout() {
  const user = useContext(AuthContext);
  useAuthGuard(user);
  return (
    <>
      <nav>
        <Link to="/">Home</Link> |<Link to="about-us">About us</Link> |
        <Link to="task-board">Task Board</Link> |
        <Link to="manage-task">Manage Task</Link> | <Link to="login">Login</Link>{" "}
        |<Link to="signup">Signup</Link>
      </nav>
      <Outlet />
    </>
  );
}

function App() {
  return (
    <AuthProvider>
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<AppLayout />}>
            <Route index element={<HomePage />} />
            <Route path="about-us" element={<AboutUsPage />} />
            <Route path="task-baord" element={<TaskBaordPage />} />
            <Route path="manage-task" element={<ManageTaskPage />}>
              <Route path="create" element={<CreateTaskPage />} />
              <Route path="schedule" element={<ScheduleTaskPage />} />
            </Route>
            <Route path="login" element={<LoginPage />} />
            <Route path="signup" element={<SignupPage />} />
            <Route path="*" element={<NoPage />} />
          </Route>
        </Routes>
      </BrowserRouter>
    </AuthProvider>
  );
}

export default App;
